import React ,{useEffect,useState} from 'react'
import { Container, Table } from 'react-bootstrap'
import { Link ,useHistory} from 'react-router-dom';
import './homeStyle.css'
import NavBar from '../NavBar';
export default function Home() {
     const users = [
          {id:1, product: 'Artist hot', seller: 'Anil', topBid: '1000$', timeRemaining: "10" },
          {id:2, product: 'My soul', seller: 'Bruce', topBid: '500$', timeRemaining: "5" },
          {id:3, product: 'Tim soul', seller: 'Seter', topBid: '600$', timeRemaining: "6" },
          {id:4, product: 'Sam', seller: 'Sam', topBid: '400$', timeRemaining: "10" },
          {id:5, product: 'Sam', seller: 'Sam', topBid: '400$', timeRemaining: "10" },
          {id:6, product: 'Sam', seller: 'Sam', topBid: '400$', timeRemaining: "10" },
          {id:7, product: 'Tim soul', seller: 'Seter', topBid: '600$', timeRemaining: "6" },
          {id:8, product: 'Tim soul', seller: 'Seter', topBid: '600$', timeRemaining: "6" },
          {id:9, product: 'Tim soul', seller: 'Seter', topBid: '600$', timeRemaining: "6" },
          {id:10, product: 'Tim soul', seller: 'Seter', topBid: '600$', timeRemaining: "0" },

     ]
     const history=useHistory()
     const [array, setArray] = useState([])
     useEffect(() => {
          var array=setArray(users.sort((first_Argument, second_Argument) => parseFloat(first_Argument.timeRemaining) - parseFloat(second_Argument.timeRemaining)));
          console.log("This Time remaing",array)
     }, [])
     const handleItem=(e,index)=>{
          console.log("This is e",e)
          history.push({
             
               state: { auctionItemData: e }
             });
     }
     const handleDelete=(e)=>{
          console.log("Thid is d",e.target.value)
     }
     
     return (
          <Container>
               <div>
                    <NavBar title="Current Auction" />
                    <Table striped bordered hover >
                         <tbody>
                              <tr>
                                   <td><b>Product</b></td>
                                   <td><b>Seller</b></td>
                                   <td><b>Top Bid</b></td>
                                   <td><b>Time Remaining</b></td>
                                   <td><b>Delete</b></td>


                              </tr>
                              {
                                  array.map((item, index) =>

                                        <tr key={index}>
                                             
                                             <td onClick={()=>handleItem(item,index)}>{item.product}</td>
                                             <td>{item.seller}</td>
                                             <td>{item.topBid}</td>
                                             <td>{item.timeRemaining}</td>
                                             <td ><Link>Delete</Link></td>


                                        </tr>
                                   )
                              }
                         </tbody>
                    </Table>
                    <div className='lower_Home_Page'>
                         <Link to='/newAuction'><button type="button" class="btn btn-primary">New Auction</button></Link>
                         <p>Your current wallet: 1000$</p>
                    </div>
               </div>
          </Container>
     );
}
