import React, { useEffect, useState } from 'react'
import { Container, Table } from 'react-bootstrap'
import { Link, useHistory } from 'react-router-dom';
import { collection, getDocs } from "firebase/firestore";
import { db } from '../../firebase';
import './homeStyle.css'
import NavBar from '../NavBar';
export default function Home() {
     const userDetail = JSON.parse(localStorage.getItem('User'))
     const users = [
          { id: 1, product: 'Artist hot', seller: 'Anil', topBid: '1000$', timeRemaining: "10" },
          { id: 2, product: 'My soul', seller: 'Bruce', topBid: '500$', timeRemaining: "5" },
          { id: 3, product: 'Tim soul', seller: 'Seter', topBid: '600$', timeRemaining: "6" },
          { id: 4, product: 'Sam', seller: 'Sam', topBid: '400$', timeRemaining: "10" },
          { id: 5, product: 'Sam', seller: 'Sam', topBid: '400$', timeRemaining: "10" },
          { id: 6, product: 'Sam', seller: 'Sam', topBid: '400$', timeRemaining: "10" },
          { id: 7, product: 'Tim soul', seller: 'Seter', topBid: '600$', timeRemaining: "6" },
          { id: 8, product: 'Tim soul', seller: 'Seter', topBid: '600$', timeRemaining: "6" },
          { id: 9, product: 'Tim soul', seller: 'Seter', topBid: '600$', timeRemaining: "6" },
          { id: 10, product: 'Tim soul', seller: 'Seter', topBid: '600$', timeRemaining: "0" },

     ]
     const history = useHistory()
     const [array, setArray] = useState([])
     const [data, setData] = useState([])
     const [loader, setLoader] = useState(true)
     let list = []

     useEffect(() => {
          var array = setArray(users.sort((first_Argument, second_Argument) => parseFloat(first_Argument.timeRemaining) - parseFloat(second_Argument.timeRemaining)));
          console.log("This Time remaing", array)

          //Get all the Data from the Firebase
          const fetchData = async () => {
               try {
                    const querySnapshot = await getDocs(collection(db, "Auction"));
                    querySnapshot.forEach((doc, index) => {
                         list.push(doc.data())
                         let date_1 = new Date(doc.data().endDateforBiding);
                         let date_2 = new Date();

                              let difference = date_1.getTime() - date_2.getTime();
                              let TotalDays = Math.ceil(difference / (1000 * 3600 * 24));
                               TotalDays;
                         
                         
                         console.log(days(date_1, date_2) + " days to world cup");
                    })
                    setData(list)
                    setLoader(false)
               } catch (error) {
                    console.log("The error is", error)
               }

          }
          fetchData()

          ///
          data.filter((ls, index) => {
               console.log("This is Calling")

          })
          ////
          handleDates()

     }, [])
     console.log("The Data is", data)
     const handleItem = (e, index) => {
          console.log("This is e", e)
          history.push({
               pathname: "/bid",
               state: { auctionItemData: e }
          });
     }
     const handleDates = () => {

     }
     const handleDelete = (e) => {
          console.log("Thid is d", e.target.value)
     }

     return (


          <Container>
               <main>
                    <NavBar title="Current Auction" />
                    {
                         loader ?
                              <div class="d-flex justify-content-center">
                                   <div class="spinner-grow d-flex " role="status">
                                        <span class="sr-only">Loading...</span>
                                   </div>
                              </div>
                              :
                              <>
                                   <Table striped bordered hover >
                                        <tbody>
                                             <tr>
                                                  <td><b>Product</b></td>
                                                  <td><b>Seller</b></td>
                                                  <td><b>Top Bid</b></td>
                                                  <td><b>Time Remaining</b></td>
                                                  <td><b>Delete</b></td>


                                             </tr>
                                             {
                                                  data && data.map((item, index) =>

                                                       <tr key={index}>

                                                            <td onClick={() => handleItem(item, index)} ><span className="product">{item.productName}</span></td>
                                                            <td>{item.sellerName}</td>
                                                            <td>{item.topBid}</td>
                                                            <td>{item.endDateforBiding}</td>
                                                            <td >{userDetail.userId === item.sellerID ? <span className="delete">Delete</span> : null}</td>
                                                       </tr>
                                                  )
                                             }
                                        </tbody>
                                   </Table>
                                   <footer className='lower_Home_Page'>
                                        <Link to='/newAuction'><button type="button" className="btn btn-primary">New Auction</button></Link>
                                        <p>Your current wallet: <span className='wallet'>1000$</span></p>
                                   </footer>
                              </>
                    }

               </main>
          </Container>
     );
}
